import React from "react";

function Dice(props) {

        const [diceFace, setDiceFace] = useState()

        useEffect (() => {
            const sides = ['one', 'two', 'three', 'four', 'five', 'six']
            const draw = sides[Math.floor(Math.random() * sides.length)]
            setDiceFace(draw)
        })

  return (
    <>
        <i className={`dice fas fa-dice-${props.diceFace}`}></i>
        <Dice className={`dice face-${diceFace}`} />
            <button onClick={() => setDiceFace}>Roll Dice</button>
    </>
  );
}

export default Dice;
