import React, { useEffect, useState } from 'react';
import Dice from './Dice'

function RollDice (){
    const [draw, setDraw] = useState(
        { sides: ['one', 'two', 'three', 'four', 'five', 'six']}
    )

    useEffect (() => {
        setDraw(Math.round(Math.random()*5) + 1) 
    })

    return (
        <>
            <Dice face={draw} />
        </>
    )
}

export default RollDice
